template.value:
// Minimal example
PositionalRelationship({
  definitions: (".Article-Paragraph" { retireFromIt: { y: 8px }, pushItFromSelf: { y: 16px } })
})

// With context
PositionalRelationship({
  definitions: ("h2" { retireFromIt: { y: 8px }, pushItFromSelf: { y: 16px } })
  contextSelector: ".Article",
  referenceElementSelector: "p"
})

// All functionality used
PositionalRelationship({
  definitions:\
      (".ContextSelector-ElementB" { 
        retireFromIt: { x: 2px, y: 4px }, 
        pushItFromSelf: { x: 6px, y: 8px }, 
        usePaddingsInsteadOfMargins: true 
      })\
      (".ContextSelector-ElementC" { retireFromIt: { x: 10px, y: 12px }, pushItFromSelf: { x: 14px, y: 16px } }),
  contextSelector: ".ContextSelector",
  referenceElementSelector: ".ContextSelector-ElementA"
})

// TODO Make sure that no whitespaces after backslashes, the line breakers
// TODO Make sure that there are no training commas

// Minimal example
retireFrom({ targetElementSelector: ".BlockName-ElementName", y: 8px })

// With context
retireFrom({
  targetElementSelector: "h1",
  y: 14px,
  referenceElementSelector: "p",
  contextSelector: ".Article"
})

// Multiple definitions
retireFrom({
  definitions:\
      ("h1" { x: 2px, y: 4px })\
      ("h2" { x: 6px, y: 8px, usePaddingsInsteadOfMargins: true }),
  referenceElementSelector: "p",
  contextSelector: ".Article"
})$END$

// Minimal example
pushTargetFromSelf({ targetElementSelector: ".BlockName-ElementName", y: 4px })

// With context
pushTargetFromSelf({
  targetElementSelector: "h2",
  y: 24px,
  referenceElementSelector: "p",
  contextSelector: ".Article"
})

// Multiple definitions
pushTargetFromSelf({
  definitions:\
    (".Example3-ElementA" { x: 2px, y: 4px })\
    (".Example3-ElementB" { x: 6px, y: 8px, usePaddingsInsteadOfMargins: true })
})$END$

// Minimal example
retireFromElementWithSameSelector({ y: $END$px })

// With context
retireFromElementWithSameSelector({ y: 8px, contextSelector: ".Block ul", referenceElementSelector: "li" })

// All functionality used
retireFromElementWithSameSelector({
  x: 4px,
  y: 8px,
  usePaddingsInsteadOfMargins: true,
  contextSelector: ".Block ol",
  referenceElementSelector: "li"
})
